implementation.field:
var node = document.createElement("label");
node.className = "searchbox-token";
node.setAttribute("mousethrough", "never");
node;

property.setter:

this.mIgnoreInput = true;
if (val) {
  // clear the emptyText _before_ setting a new non-empty value
  try {
    this._clearEmptyText();
  }
  catch (e) {}
  this.inputField.value = val;
}
else {
  // display the emptyText _after_ setting a value that's an empty string
  this.inputField.value = val;
  try {
    this._updateVisibleText();
  }
  catch (e) {}
}
this.mIgnoreInput = false;

var event = document.createEvent('Events');
event.initEvent('ValueChange', true, true);
this.inputField.dispatchEvent(event);

this.rebuildTokens();
if (val && !this.hasAttribute("focused")) {
  this._showTokens();
}
else {
  this._hideTokens();
}
return val;

method.body:
if (aEvent.button != 0) {
  return;
}
aEvent.stopPropagation();

// Adjust token visibility
var tokensScrollBoxObject = this.tokensContainer.boxObject.QueryInterface(Components.interfaces.nsIScrollBoxObject);
var rightLimitX = tokensScrollBoxObject.x + tokensScrollBoxObject.width - this.tokensOverflowButton.boxObject.width;

for (var i = 0; i < this.tokensContainer.childNodes.length; i++) {
  var tokenNode = this.tokensContainer.childNodes[i];
  var tokenPopupNode = this.tokensOverflowPopupContainer.childNodes[i];
  if ((tokenNode.boxObject.x + tokenNode.boxObject.width) > rightLimitX) {
    tokenPopupNode.style.display = "-moz-box";
  }
  else {
    tokenPopupNode.style.display = "none";
  }
}

var popup = document.getAnonymousElementByAttribute(this, "anonid", "tokens-overflow-popup");
popup.hidden = false;
popup.openPopup(this.tokensOverflowLayer, 'after_end');

while (this.tokensContainer.hasChildNodes()) {
  this.tokensContainer.removeChild(this.tokensContainer.firstChild);
}

while (this.tokensOverflowPopupContainer.hasChildNodes()) {
  this.tokensOverflowPopupContainer.removeChild(this.tokensOverflowPopupContainer.firstChild);
}

if (this.value) {
  var tokensScrollBoxObject = this.tokensContainer.boxObject.QueryInterface(Components.interfaces.nsIScrollBoxObject);

  var termsArray = searchwp.Tokenizer.createTokensArray(this.value);
  for (var i = 0, j = 0; i < termsArray.length; i++) {
    var tokenNode = this.tokenProto.cloneNode(true);
    tokenNode.value = termsArray[i];
    tokenNode.setAttribute("tooltiptext", this.stringbundle.getFormattedString("tokenTooltiptext", [termsArray[i]], 1));

    if (searchwp.Preferences.highlighted && termsArray[i].length >= searchwp.Preferences.highlightMinLength) {
      var criteria = "term-" + ((j++ % searchwp.Preferences.highlighterCount) + 1);
      tokenNode.setAttribute("highlight", criteria);
    }
    this.tokensContainer.appendChild(tokenNode);

    var tokenPopupNode = tokenNode.cloneNode(true);
    this.tokensOverflowPopupContainer.appendChild(tokenPopupNode);
  }

  searchwp.Highlighting.update(termsArray);
}
else {
  searchwp.Highlighting.update();
}

if (!this.hasAttribute("tokenized") && this.value) {
  this.tokensLayer.hidden = false;
  this.tokensOverflowLayer.hidden = true;
  this.inputLayer.style.setProperty("opacity", "0", "important");
  this.setAttribute("tokenized", true);

  var event = document.createEvent('Events');
  event.initEvent('tokenized', true, true);
  this.dispatchEvent(event);
}

if (this.hasAttribute("tokenized")) {
  this.tokensLayer.hidden = true;
  this.tokensOverflowLayer.hidden = true;
  this.inputLayer.style.removeProperty("opacity");
  this.removeAttribute("tokenized");

  var event = document.createEvent('Events');
  event.initEvent('untokenized', true, true);
  this.dispatchEvent(event);
}

handlers.handler:
event.stopPropagation();
if (event.button == 2) {
  event.preventDefault();
}

var findBackwards = event.shiftKey;
if (event.button >= 1 && event.button <= 2) {
  findBackwards = !findBackwards;
}

var matchCase = event.altKey || event.ctrlKey;

// To handle F3 correctly, we have to clear this find bar search.
if (gFindBar.hidden && gFindBar.getElement("findbar-textbox").value.length > 0) {
  gFindBar.getElement("findbar-textbox").value = "";
  gFindBar.find("");
}

var fastFind = window.getBrowser().fastFind;
fastFind.caseSensitive = matchCase;

var result;
if (fastFind.searchString != this.value) {
  result = fastFind.find(this.value, false);
}
else {
  result = fastFind.findAgain(findBackwards, false);
}

var stringbundle = document.getBindingParent(this).stringbundle;
switch (result) {
  case 0: // Found
    break;
  case 1: // Not found
    this.setAttribute("notfound", true);
    if (this._notFoundTimeout) {
      clearTimeout(this._notFoundTimeout);
    }

    this._notFoundTimeout = setTimeout(function(aToken, aEvent) {
      aToken.removeAttribute("notfound");
    }, 3000, this);

    searchwp.displayMessage(stringbundle.getFormattedString("notFound", [this.value], 1), true);
    break;
  case 2: // Wrapped
    if (findBackwards) {
      searchwp.displayMessage(stringbundle.getString("wrappedToBottom"), true);
    }
    else {
      searchwp.displayMessage(stringbundle.getString("wrappedToTop"), true);
    }
    break;
}
